mapper.delete:
delete from
    menu_category_tb
where
    admin_id = #{adminId}
    and menu_category_id = #{menuCategoryId}

delete from
    menu_tb
where
    admin_id = #{adminId}
    and menu_id = #{menuId}

mapper.update:
update
    option_title_tb
set
    menu_id = #{menuId},
    title_name = #{titleName}
    update_date = now()
where
    option_title_id = #{optionTitleId}
    and admin_id = #{adminId}

update
    option_name_tb
set
    menu_id = #{menuId},
    option_name = #{optionName}
    title_id = #{titleId}
    update_date = now()
where
    option_name_id = #{optionNameId}
    and admin_id = #{adminId}
    and title_id = #{titleId}

mapper.delete:
delete from
    option_title_tb
where
    option_title_id = #{optionTitleId}
    and admin_id = #{adminId}

delete from
    option_name_tb
where
    option_name_id = #{optionNameId}
    and admin_id = #{adminId}
