mapper.select:
select mt.*, mct.menu_category_name from menu_tb mt left outer join menu_category_tb mct on (mct.menu_category_id = mt.menu_category_id) where mt.admin_id = #{adminId}
and mt.menu_category_id = #{menuCategoryId}
order by mt.menu_category_id

select at.admin_id, at.company_name, mt.menu_id, mt.menu_code, mct.menu_category_name, mt.menu_name, mt.menu_price, mt.menu_state, mt.recommend_menu from menu_tb mt left outer join admin_tb at on (at.admin_id = mt.admin_id) left outer join menu_category_tb mct on (mct.menu_category_id = mt.menu_category_id) where at.admin_id = #{adminId}
and mt.menu_name like CONCAT('%', #{menuName}, '%')
and mt.menu_code like CONCAT('%', #{menuCode}, '%')
and mt.menu_state = #{menuState}
and mt.recommend_menu = #{recommendMenu}
and mt.menu_category_id = #{categoryId}
order by mt.menu_id;

mapper.delete:
delete from
    menu_category_tb
where
    admin_id = #{adminId}
    and menu_category_id = #{menuCategoryId}

mapper.update:
update
    option_title_tb
set
    menu_id = #{menuId},
    title_name = #{titleName}
    update_date = now()
where
    option_title_id = #{optionTitleId}
    and admin_id = #{adminId}

update
    option_name_tb
set
    menu_id = #{menuId},
    option_name = #{optionName},
    option_price = #{optionPrice},
    title_id = #{titleId}
    update_date = now()
where
    option_name_id = #{optionNameId}
    and admin_id = #{adminId}
    and menu_id = #{menuId},
    and title_id = #{titleId}

mapper.delete:
delete from
    option_title_tb
where
    option_title_id = #{optionTitleId}
    and admin_id = #{adminId}

delete from
    option_name_tb
where
    option_name_id = #{optionNameId}
    and admin_id = #{adminId}
