mapper.delete:
delete from
    tables_tb
where
    admin_id = #{adminId}
    and floor_num = #{floorNum};

delete from
    floors_tb
where
    admin_id = #{adminId}
    and floor_num = #{floorNum}

mapper.update:
update
    tables_tb
set
    tables_name = #{tablesName},
    update_date = now()
where
    admin_id = #{adminId}
    and floor_num = #{floorNum}
    and tables_num = #{tablesNum}
